import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import './App.css';

const ExpenseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [expense, setExpense] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/expenses/${id}`)
      .then((res) => setExpense(res.data))
      .catch((err) => console.error("Failed to fetch expense:", err));
  }, [id]);

  const handleDelete = () => {
    axios
      .delete(`http://localhost:5000/api/expenses/${id}`)
      .then(() => navigate("/"))
      .catch((err) => console.error("Failed to delete expense:", err));
  };

  if (!expense) return <div>Loading...</div>;

  return (
    <div className="container">
      <h3>Amount: ${parseFloat(expense.amount).toFixed(2)}</h3>
      <p>Category: {expense.category}</p>
      <p>Date: {expense.date}</p>
      <p>Added: {new Date(expense.created_at).toLocaleString()}</p>
      <button onClick={() => navigate(`/edit/${id}`)}>Edit Expense</button>
      <button onClick={handleDelete}>Delete Expense</button>
    </div>
  );
};

export default ExpenseDetail;
